import { Heading, Pane, Text, Code } from 'evergreen-ui';
import React from 'react';
import { Helmet } from 'react-helmet';
import YouTube from 'react-youtube';
import SpotifyPlayer from 'react-spotify-web-playback';

const Spacer = ({ size = 1 }) => (
  <Pane width={size * 8} height={size * 8} flexShrink={0} />
);

function Home() {
  return (
    <React.Fragment>
      <Helmet>
        <title>
          aster haven (@therebelrobot) - software, hardware, gardenware
        </title>
      </Helmet>
      <style global jsx>{`
        @import url(https://cdnjs.cloudflare.com/ajax/libs/normalize/8.0.1/normalize.min.css);
        @import url('https://fonts.googleapis.com/css?family=Cabin&display=swap');
        * {
          font-family: 'Cabin', sans-serif !important;
        }
        body,
        html {
          background: white;
          width: 100%;
          height: 100%;
          display: flex;
          flex-direction: row;
          align-items: center;
          justify-content: center;
          font-family: 'Cabin', sans-serif !important;
          background: url(/static/what-the-hex.png);
        }
      `}</style>
      <Pane textAlign="center">
        <Heading size={900}>aster haven</Heading>
        <Heading size={300}>they/them or she/her</Heading>
        <Spacer size={3} />
        <Pane
          display="flex"
          flexDirection="row"
          flexWrap="wrap"
          justifyContent="center"
        >
          <Pane border="default" padding={8} maxWidth={300}>
            <Heading>software</Heading>
            <Spacer size={3} />
            <Text>
              full-stack web development, node.js, react.js, docker,
              infrastructure, systems administration, devops and scaling
            </Text>
            <Spacer size={3} />
            <Heading>medium.com</Heading>
            <Heading size={300}>sr software engineer</Heading>
            <Text>2019-present</Text>
            <Spacer size={3} />
            <Heading>san francisco gay men's chorus</Heading>
            <Heading size={300}>archive team</Heading>
            <Text>2018-present</Text>
            <Spacer size={3} />
            <Heading>saleae</Heading>
            <Heading size={300}>sr software engineer</Heading>
            <Text>2017-2019</Text>
            <Spacer size={3} />
            <Heading>segment</Heading>
            <Heading size={300}>software engineer</Heading>
            <Text>2016-2017</Text>
            <Spacer size={3} />
            <Heading>code for san francisco</Heading>
            <Heading size={300}>core team</Heading>
            <Text>2015-2017</Text>
            <Spacer size={3} />
            <Heading>mashape</Heading>
            <Heading size={300}>software engineer</Heading>
            <Text>2015-2016</Text>
            <Spacer size={3} />
            <Heading>node.js website working group</Heading>
            <Text>2014-2015</Text>
            <Spacer size={3} />

              <Text>contact for additional information about employment history, including
              independent contracting, business ventures, or for a full resume,
              please reach out at <Code>inquiries</Code><Code>at</Code><Code>therebelrobot.com</Code></Text>
          </Pane>
          <Spacer size={3} />
          <Pane border="default" padding={8} maxWidth={300}>
            <Heading>music</Heading>
            <Heading size={300}>san francisco gay men's chorus</Heading>
            <Text>the san francisco gay men's chorus provides community support through outreach, performances, and anti-bullying curriculum to nearby schools.</Text>
            <Text>2017-2018, Low Bass.</Text>
            <YouTube videoId="XuX7RLYxMVI" opts={{width: '284', height: '159.75'}} />
            <Heading size={300}>theForgeProject</Heading>
            <Text>a musical side-project, producing smooth rhythmic future-bass with a strong walking beat.</Text>
            <Text>started in 2016.</Text>
            <iframe src="https://open.spotify.com/embed/artist/76zOPgY1LiqJjT19OteunL" width="300" height="175" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
            <iframe src="https://open.spotify.com/embed/artist/4a56tUWRygifILXCoXAcoP" width="300" height="330" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
            <Text>For usage rights or booking, please reach out at inquiriesattherebelrobot.com</Text>
          </Pane>
        </Pane>
      </Pane>
      <Pane maxWidth={608} border="default">

      </Pane>
    </React.Fragment>
  );
}

export default Home;
